package org.transmart.plugin.auth0

import grails.plugin.springsecurity.SpringSecurityUtils
import groovy.transform.CompileDynamic
import groovy.transform.CompileStatic
import org.springframework.beans.factory.InitializingBean
import org.springframework.beans.factory.annotation.Autowired
import org.springframework.beans.factory.annotation.Value

import javax.servlet.ServletContext

/**
 * @author <a href='mailto:burt_beckwith@hms.harvard.edu'>Burt Beckwith</a>
 */
@CompileStatic
class Auth0Config implements InitializingBean {

	String auth0CallbackUrl
	String auth0ClientId
	String auth0ClientSecret
	String auth0Domain
	String instanceTypeSuffix
	String irctFullUrl
	boolean isTOS
	String redirectOnLogout
	String redirectOnSuccess
	String uiHeroImageUrl
	boolean useRecaptcha

	String webtaskBaseUrl

	@Value('${edu.harvard.transmart.access.level1:}')
	String accessLevel1

	@Value('${com.recomdata.searchtool.appTitle:}')
	String appTitle

	@Value('${edu.harvard.transmart.captcha.secret:}')
	String captchaSecret

	@Value('${edu.harvard.transmart.captcha.sitekey:MissingCaptchaSiteKey}')
	String captchaSitekey

	@Value('${edu.harvard.transmart.captcha.verifyurl:}')
	String captchaVerifyUrl

	@Value('${edu.harvard.transmart.email.logo:}')
	String emailLogo

	@Value('${edu.harvard.transmart.email.notify:}')
	String emailNotify

	@Value('${edu.harvard.transmart.googleanalytics.tracking:}')
	String googleAnalyticsTracking

	@Value('${com.recomdata.guestAutoLogin:false}')
	boolean guestAutoLogin

	@Value('${com.recomdata.guestUserName:}')
	String guestUserName

	@Value('${edu.harvard.transmart.instance.name:i2b2/tranSMART}')
	String instanceName

	@Value('${edu.harvard.transmart.instance.type:}')
	String instanceType

	@Value('${edu.harvard.transmart.irct.host:https://localhost}')
	String irctHost

	@Value('${edu.harvard.transmart.irct.url:/rest/v1/securityService/createKey}')
	String irctUrl

	@Value('${loginBannerMessage:Please Login Below.}')
	String loginBannerMessage

	@Value('${com.recomdata.userSignupEnabled:true}')
	boolean userSignupEnabled

	@Autowired private AuthService authService
	@Autowired private ServletContext servletContext

	String pmsdnLogo = "data:image/png;base64, "

	@CompileDynamic
	void afterPropertiesSet() {
		def conf = SpringSecurityUtils.securityConfig
		redirectOnLogout = conf.logout.filterProcessesUrl
		redirectOnSuccess = conf.successHandler.defaultTargetUrl

		conf = conf.auth0
		auth0CallbackUrl = conf.loginCallback
		auth0ClientId = conf.clientId
		auth0ClientSecret = conf.clientSecret
		auth0Domain = conf.domain
		useRecaptcha = conf.useRecaptcha
		webtaskBaseUrl = conf.webtaskBaseUrl

		instanceTypeSuffix = instanceType ? '_' + instanceType : ''
		irctFullUrl = irctHost + irctUrl

		uiHeroImageUrl = servletContext.contextPath + '/images/' +
				(instanceType == 'pmsdn' ? 'pmsdnHeroImage' : 'transmartlogo') + '.jpg'
	}

	/**
	 * Initialization that cannot happen in <code>afterPropertiesSet()</code>
	 * (e.g. GORM calls) because not everything is ready yet.
	 */
	void init() {
		isTOS = authService.setting('tos.text') != null
	}
}
